// ==UserScript==
// @name         nanobanana watermark remover
// @namespace    http://tampermonkey.net/
// @version      1.2
// @description  Automatically removes watermarks in-place and provides efficient high-res download options.
// @author       tizee
// @icon         https://www.google.com/s2/favicons?domain=gemini.google.com
// @match        https://gemini.google.com/*
// @grant        GM_xmlhttpRequest
// @connect      googleusercontent.com
// @run-at       document-end
// @updateURL    https://github.com/tizee-tampermonkey-scripts/nanobanana-watermark-remover/raw/main/user.js
// @downloadURL  https://github.com/tizee-tampermonkey-scripts/nanobanana-watermark-remover/raw/main/user.js
// @homepageURL  https://github.com/tizee-tampermonkey-scripts/nanobanana-watermark-remover
// @supportURL   https://github.com/tizee-tampermonkey-scripts/nanobanana-watermark-remover/issues
// ==/UserScript==

(function() {
    'use strict';

    // --- Assets (Base64) ---
    const BG_48_DATA = 'data:image/png;base64,';
    const BG_96_DATA = 'data:image/png;base64,';

    // --- Styles ---
    const styles = `
        img[data-watermark-processed="processing"] {
            filter: blur(5px) grayscale(0.5);
            transition: filter 0.3s;
        }
    `;

    // --- Core Logic ---
    function calculateAlphaMap(bgCaptureImageData) {
        const { width, height, data } = bgCaptureImageData;
        const alphaMap = new Float32Array(width * height);
        for (let i = 0; i < alphaMap.length; i++) {
            const idx = i * 4;
            const r = data[idx];
            const g = data[idx + 1];
            const b = data[idx + 2];
            const maxChannel = Math.max(r, g, b);
            alphaMap[i] = maxChannel / 255.0;
        }
        return alphaMap;
    }

    const ALPHA_THRESHOLD = 2e-3;
    const MAX_ALPHA = 0.99;
    const LOGO_VALUE = 255;

    function removeWatermark(imageData, alphaMap, position) {
        const { x, y, width, height } = position;
        for (let row = 0; row < height; row++) {
            for (let col = 0; col < width; col++) {
                const imgIdx = ((y + row) * imageData.width + (x + col)) * 4;
                const alphaIdx = row * width + col;
                let alpha = alphaMap[alphaIdx];
                if (alpha < ALPHA_THRESHOLD) continue;
                alpha = Math.min(alpha, MAX_ALPHA);
                const oneMinusAlpha = 1.0 - alpha;
                for (let c = 0; c < 3; c++) {
                    const watermarked = imageData.data[imgIdx + c];
                    const original = (watermarked - alpha * LOGO_VALUE) / oneMinusAlpha;
                    imageData.data[imgIdx + c] = Math.max(0, Math.min(255, Math.round(original)));
                }
            }
        }
    }

    function detectWatermarkConfig(imageWidth, imageHeight) {
        if (imageWidth > 1024 && imageHeight > 1024) {
            return { logoSize: 96, marginRight: 64, marginBottom: 64 };
        } else {
            return { logoSize: 48, marginRight: 32, marginBottom: 32 };
        }
    }

    function calculateWatermarkPosition(imageWidth, imageHeight, config) {
        const { logoSize, marginRight, marginBottom } = config;
        return {
            x: imageWidth - marginRight - logoSize,
            y: imageHeight - marginBottom - logoSize,
            width: logoSize,
            height: logoSize
        };
    }

    class WatermarkEngine {
        constructor(bgCaptures) {
            this.bgCaptures = bgCaptures;
            this.alphaMaps = {};
        }

        static async create() {
            const bg48 = await loadImage(BG_48_DATA);
            const bg96 = await loadImage(BG_96_DATA);
            return new WatermarkEngine({ bg48, bg96 });
        }

        async getAlphaMap(size) {
            if (this.alphaMaps[size]) return this.alphaMaps[size];
            const bgImage = size === 48 ? this.bgCaptures.bg48 : this.bgCaptures.bg96;
            const canvas = document.createElement('canvas');
            canvas.width = size;
            canvas.height = size;
            const ctx = canvas.getContext('2d');
            ctx.drawImage(bgImage, 0, 0);
            const imageData = ctx.getImageData(0, 0, size, size);
            const alphaMap = calculateAlphaMap(imageData);
            this.alphaMaps[size] = alphaMap;
            return alphaMap;
        }

        async removeWatermarkFromImage(image) {
            const canvas = document.createElement('canvas');
            canvas.width = image.width;
            canvas.height = image.height;
            const ctx = canvas.getContext('2d');
            ctx.drawImage(image, 0, 0);
            const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
            const config = detectWatermarkConfig(canvas.width, canvas.height);
            const position = calculateWatermarkPosition(canvas.width, canvas.height, config);
            const alphaMap = await this.getAlphaMap(config.logoSize);
            removeWatermark(imageData, alphaMap, position);
            ctx.putImageData(imageData, 0, 0);
            return canvas;
        }
    }

    let engine = null;
    const processingQueue = new Set();
    const processedImages = new WeakSet(); // Track processed images more efficiently

    // Process all images, skipping those already in queue
    const processAllImages = () => {
        const images = findGeminiImages();
        if (images.length === 0) return 0;
        console.debug(`[Gemini Remover] Found ${images.length} images to process`);
        images.forEach(processImage);
    };

    const loadImage = (src) => new Promise((resolve, reject) => {
        const img = new Image();
        img.onload = () => resolve(img);
        img.onerror = reject;
        img.src = src;
    });

    const canvasToBlob = (canvas, type = 'image/png') =>
        new Promise(resolve => canvas.toBlob(resolve, type));

    const isValidGeminiImage = (img) => {
        return img.closest('generated-image,.generated-image-container') !== null;
    };

    const findGeminiImages = () => {
        return [...document.querySelectorAll('img[src*="googleusercontent.com"]')].filter(isValidGeminiImage);
    };

    const getHighResUrl = (src) => {
      return src.replace(/=s\d+(?=[-?#]|$)/, "=s0");
    };

    async function fetchImageAsBlob(url) {
        return new Promise((resolve, reject) => {
            GM_xmlhttpRequest({
                method: "GET",
                url: url,
                responseType: "blob",
                onload: function(response) {
                    if (response.status >= 200 && response.status < 300) resolve(response.response);
                    else reject(new Error(`Fetch failed: ${response.status}`));
                },
                onerror: reject
            });
        });
    }

    // --- Automatic Replacement Logic ---
    async function processImage(img) {
        if (!engine || processingQueue.has(img)) return;

        processingQueue.add(img);
        img.dataset.watermarkProcessed = 'processing';

        try {
            const originalSrc = img.src;
            img.src = '';
            const highResBlob = await fetchImageAsBlob(getHighResUrl(originalSrc));
            if (highResBlob) {
                const highResBlobUrl = URL.createObjectURL(highResBlob);
                const highResImg = await loadImage(highResBlobUrl);
                const processedCanvas = await engine.removeWatermarkFromImage(highResImg);
                const cleanBlob = await canvasToBlob(processedCanvas);
                URL.revokeObjectURL(highResBlobUrl);

                // Store original and replace in-place
                img.src = URL.createObjectURL(cleanBlob);
                img.dataset.originalSrc = originalSrc;
                img.dataset.watermarkProcessed = 'true';
                console.debug("[Gemini Remover] Processed image", img.src);
            } else {
                img.dataset.watermarkProcessed = 'failed';
                img.src = originalSrc;
                console.debug("[Gemini Remover] Failed to process image:", e);
            }
        } catch (e) {
            img.dataset.watermarkProcessed = 'failed';
            img.src = originalSrc;
            console.debug("[Gemini Remover] Failed to process image:", e);
        } finally {
            processingQueue.delete(img);
        }
    }

    // --- Intercept Fetch ---
    const GEMINI_URL_PATTERN = /^https:\/\/lh3\.googleusercontent\.com\/rd-gg(?:-dl)?\/.+=s(?!0-d\?).*/;
    const { fetch: origFetch } = unsafeWindow;
    unsafeWindow.fetch = async (...args) => {
        const url = typeof args[0] === "string" ? args[0] : args[0]?.url;
        if (GEMINI_URL_PATTERN.test(url) ) {
            console.debug("[Gemini] Intercepting:", url);
            const highResUrl = getHighResUrl(url);
            if (typeof args[0] === "string") args[0] = highResUrl;
            else if (args[0]?.url) args[0].url = highResUrl;

            const response = await origFetch(...args);
            if (!response.ok || !engine) return response;

            try {
                const blob = await response.blob();
                const bitmap = await createImageBitmap(blob);
                const canvas = await engine.removeWatermarkFromImage(bitmap);
                const processedBlob = await canvasToBlob(canvas);
                return new Response(processedBlob, {
                    status: response.status,
                    statusText: response.statusText,
                    headers: response.headers
                });
            } catch (e) {
                console.debug("Processing failed:", e);
                return response;
            }
        }
        return origFetch(...args);
    };

    // --- Mutation Logic ---
    const debounce = (fn, delay) => {
        let timeout;
        return (...args) => {
            clearTimeout(timeout);
            timeout = setTimeout(() => fn(...args), delay);
        };
    };

    const setupMutationObserver = () => {
        const observer = new MutationObserver(debounce(processAllImages, 100));
        observer.observe(document.body, { childList: true, subtree: true });
        console.debug('[Gemini Downloader] Mutation observer active');
    }

    // Direct IIFE execution with init
    (async () => {
        console.debug(`[Gemini Downloader] Starting initialization`);
        try {
            engine = await WatermarkEngine.create();
            console.debug(`[Gemini Downloader] Watermark engine ready`);
        } catch (e) {
            console.error(`[Gemini Downloader] Failed to create watermark engine`, e);
            return;
        }
        processAllImages();
        setupMutationObserver();
    })();
})();
